<script lang="ts">
import { ref } from 'vue';

export default {
  name: 'NavBock',
  setup() {
    const open = ref(false);

    function hideMenu() {
      document.body.classList.remove("menu-open");
      open.value = false;
    }

    hideMenu();

    return {
      open,
      toggle() {
        open.value = !open.value;
        document.body.classList.toggle("menu-open");
      },
      hideMenu // Add the hideMenu function to the returned object
    };
  },
};
